
public class ifconutineodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i < 10; i ++) {
			//if the number is even
			//skip and continue
			
			if (i % 2 == 0) 
				continue;
			
				// if the number is odd print it 
			System.out.println(i + " ");
			}
			
		}

	}


