
public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Arithmetic operators
        int a = 10;
        int b = 30;
        
        System.out.println("a + b = " + (a + b));
        System.out.println("a - b = " + (a - b));
        System.out.println("a * b = " + (a * b));
        System.out.println("a / b = " + (a / b));
        System.out.println("a % b = " + (a % b));
	}

}
